import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {

}
<h2 class="text-2xl text-center font-semibold">For more information or to arrange a tour, contact Chris Parrott</h2>
<img src="./../../../assets/contact-image.png" alt="Portrait of Chris" class="h-56 mx-auto my-5">
<ul class="flex flex-col">
  <li class="mx-auto"><b>Email: </b> <a href="mailto:chris@chrisparrot.ca">chris@chrisparrot.ca</a></li>
  <li class="mx-auto"><b>Phone: </b> <a href="mailto:chris@chrisparrot.ca">chris@chrisparrot.ca</a></li>
  <li class="mx-auto"><b>Cell: </b> <a href="mailto:chris@chrisparrot.ca">chris@chrisparrot.ca</a></li>
  <li class="mx-auto"><b>Fax: </b> <a href="mailto:chris@chrisparrot.ca">chris@chrisparrot.ca</a></li>
  <li class="mx-auto"><b>Address: </b> <a href="mailto:chris@chrisparrot.ca">chris@chrisparrot.ca</a></li>
</ul>
