import { Component, Input, HostListener } from '@angular/core';

@Component({
  selector: 'app-slides',
  templateUrl: './slides.component.html',
  styleUrls: ['./slides.component.css']
})
export class SlidesComponent {

  @Input() photosSources: string[] = [];
  currentIndex: number = 0;
  touchstartX: number = 0;
  touchendX: number = 0;

  nextPhoto() {
    if (this.currentIndex !== this.photosSources.length - 1) {
      this.currentIndex += 1;
    } else {
      this.currentIndex = 0;
    }
  }

  prevPhoto() {
    if (this.currentIndex !== 0) {
      this.currentIndex -= 1;
    } else {
      this.currentIndex = this.photosSources.length - 1;
    }
  }

  @HostListener('touchstart', ['$event'])
  onTouchStart(event: TouchEvent) {
    this.touchstartX = event.changedTouches[0].screenX;
  }

  @HostListener('touchend', ['$event'])
  onTouchEnd(event: TouchEvent) {
    this.touchendX = event.changedTouches[0].screenX;
    this.handleGesture();
  }

  handleGesture() {
    if (this.touchendX < this.touchstartX) {
      this.nextPhoto();
    }

    if (this.touchendX > this.touchstartX) {
      this.prevPhoto();
    }
  }
}

<div style="overflow: hidden;">
  <img [src]="photosSources[currentIndex]" alt="Just an alt"
    style="transform: translateX(-{{ currentIndex * 100 }}%); transition: transform 0.5s ease;">
</div>
