import { Component, Input, ViewChild, ElementRef, Renderer2 } from '@angular/core';
import * as Hammer from 'hammerjs';
import {
  trigger,
  state,
  style,
  animate,
  transition
} from '@angular/animations';

@Component({
  selector: 'app-slides',
  templateUrl: './slides.component.html',
  styleUrls: ['./slides.component.css'],
  animations: [
    trigger('slideAnimation', [
      state('left', style({
        transform: 'translateX(-100%)',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%'
      })),
      state('center', style({
        transform: 'translateX(0)',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%'
      })),
      state('right', style({
        transform: 'translateX(100%)',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%'
      })),
      transition('* => left', [
        style({ transform: 'translateX(100%)' }),
        animate('0.3s ease-in-out')
      ]),
      transition('* => right', [
        style({ transform: 'translateX(-100%)' }),
        animate('0.3s ease-in-out')
      ])
    ])
  ]
})
export class SlidesComponent {
  @Input() photosSources: string[] = [];
  currentIndex: number = 0;
  slideDirection: string = 'center';

  nextPhoto() {
    if (this.currentIndex < this.photosSources.length - 1) {
      this.slideDirection = 'left';
      this.currentIndex += 1;
    } else {
      this.slideDirection = 'left';
      this.currentIndex = 0;
    }
  }

  prevPhoto() {
    if (this.currentIndex > 0) {
      this.slideDirection = 'right';
      this.currentIndex -= 1;
    } else {
      this.slideDirection = 'right';
      this.currentIndex = this.photosSources.length - 1;
    }
  }

  animationDone() {
    this.slideDirection = 'center';
  }

  getSlideState(index: number) {
    if (index === this.currentIndex) {
      return 'center';
    } else if (index === this.currentIndex - 1 || (index === this.photosSources.length - 1 && this.currentIndex === 0)) {
      return this.slideDirection;
    } else {
      return 'right';
    }
  }
}


<div class="slide-container" #slideContainer>
  <div class="slide" [style.background-image]="'url(' + photosSources[currentIndex] + ')'"></div>
</div>
