import { Component, Input, ViewChild, ElementRef, Renderer2 } from '@angular/core';
import * as Hammer from 'hammerjs';

@Component({
  selector: 'app-slides',
  templateUrl: './slides.component.html',
  styleUrls: ['./slides.component.css']
})
export class SlidesComponent {
  @Input() photosSources: string[] = [];
  currentIndex = 0;

  @ViewChild('slideContainer') slideContainer!: ElementRef;

  constructor(private renderer: Renderer2) { }

  ngAfterViewInit() {
    const hammer = new Hammer(this.slideContainer.nativeElement);
    hammer.get('swipe').set({ direction: Hammer.DIRECTION_HORIZONTAL });

    hammer.on('swipeleft', () => {
      this.nextPhoto();
    });

    hammer.on('swiperight', () => {
      this.prevPhoto();
    });
  }

  nextPhoto() {
    if (this.currentIndex < this.photosSources.length - 1) {
      this.currentIndex++;
      // this.updateSlide();
    }
  }

  prevPhoto() {
    if (this.currentIndex > 0) {
      this.currentIndex--;
      // this.updateSlide();
    }
  }

  updateSlide() {
    const slide = this.slideContainer.nativeElement.querySelector('.slide');
    this.renderer.setStyle(slide, 'background-image', `url('${this.photosSources[this.currentIndex]}')`);
  }
}


<div class="slide-container" #slideContainer>
  <img [src]="photosSources[currentIndex]" alt="" class="slide px-12">
</div>
<div class="flex flex-row w-full mx-auto">
  <svg *ngFor="let photo of photosSources" class="" xmlns="http://www.w2.org/2000/svg" width="24" height="24"
    viewBox="0 0 24 24">
    <path fill="currentColor"
      d="M12 22q-2.075 0-3.9-.788t-3.175-2.137q-1.35-1.35-2.137-3.175T2 12q0-2.075.788-3.9t2.137-3.175q1.35-1.35 3.175-2.137T12 2q2.075 0 3.9.788t3.175 2.137q1.35 1.35 2.138 3.175T22 12q0 2.075-.788 3.9t-2.137 3.175q-1.35 1.35-3.175 2.138T12 22Zm0-2q3.35 0 5.675-2.325T20 12q0-3.35-2.325-5.675T12 4Q8.65 4 6.325 6.325T4 12q0 3.35 2.325 5.675T12 20Zm0-8Z" />
  </svg>

</div>
<div class="flex flex-row justify-around my-5">
  <button type="button" class="rounded-md border px-8 py-2" (click)="prevPhoto()">Prev</button>
  <button type="button" class="rounded-md border px-8 py-2" (click)="nextPhoto()">Next</button>
</div>
