import { Component, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Component({
  selector: 'app-slides',
  templateUrl: './slides.component.html',
  styleUrls: ['./slides.component.css']
})
export class SlidesComponent {

  slides$ = new BehaviorSubject<string[]>(['']);

  constructor() { }

  ngOnInit(): void {
    this.slides$.next(
      Array.from({ length: 600 }).map((el, index) => `Slide ${index + 1}`)
    );
  }


}
<swiper [slidesPerView]="1" [navigation]="true" [pagination]="{ type: 'fraction' }" [keyboard]="{
    enabled: true
  }" [virtual]="true" class="my-swiper">
  <ng-template swiperSlide *ngFor="let slide of slides$ | async; index as i">
    <div class="swiper-slide-container">
      {{ slide }}
    </div>
  </ng-template>
</swiper>
